mapper.select:
SELECT
    t.id AS id, 
    t.title AS title, 
    t.content AS content, 
    t.postdate AS postdate, 
    t.poster AS poster, 
    t.video AS video, 
    t.del_flag AS delFlag
FROM
    classes t

    t.del_flag = '0'

AND t.id = #{id}


AND t.title = #{title}


AND t.content = #{content}


AND t.postdate = #{postdate}


AND t.poster = #{poster}


AND t.video = #{video}


AND t.del_flag = #{delFlag}


ORDER BY t.postdate ASC ,id ASC

mapper.delete:
DELETE FROM
    classes


    id = #{id}


AND title = #{title}


AND content = #{content}


AND postdate = #{postdate}


AND poster = #{poster}


AND video = #{video}


AND del_flag = #{delFlag}

mapper.update:
UPDATE classes
id = #{id}, title = #{title}, content = #{content}, postdate = #{postdate}, poster = #{poster}, video = #{video}, del_flag = #{delFlag}
WHERE id = #{id}
