mapper.select:
SELECT
    t.id AS id, 
    t.goodsid AS goodsid, 
    t.uid AS uid, 
    t.evaluate AS evaluate,
    t.level AS level, 
    t.yc AS yc,
    t.postdate AS postdate,
    t.del_flag AS del_flag
FROM
    evaluate t

    t.del_flag = '0'

AND t.id = #{id}


AND t.goodsid = #{goodsid}


AND t.uid = #{uid}


AND t.evaluate = #{evaluate}


AND t.level = #{level}


AND t.yc = #{yc}


AND t.postdate = #{postdate}


AND t.del_flag = #{del_flag}


ORDER BY t.postdate desc

mapper.delete:
DELETE FROM
    evaluate


    id = #{id}


AND goodsid = #{goodsid}


AND uid = #{uid}


AND evaluate = #{evaluate}


AND level = #{level}


AND yc = #{yc}


AND postdate = #{postdate}


AND del_flag = #{del_flag}

mapper.update:
UPDATE evaluate
id = #{id}, goodsid = #{goodsid}, uid = #{uid}, evaluate = #{evaluate}, level = #{level}, yc = #{yc}, postdate = #{postdate}, del_flag = #{del_flag}
WHERE id = #{id}
