mapper.select:
SELECT
    t.id AS id, 
    t.FromUInfoid AS fromuinfoid, 
    t.ToUInfoid AS touinfoid, 
    t.TradeDate AS tradedate, 
    t.state AS state, 
    t.Goodsid AS goodsid
FROM
    trade t

    t.del_flag = '0'

AND t.id = #{id}


AND t.FromUInfoid = #{fromuinfoid}


AND t.ToUInfoid = #{touinfoid}


AND t.TradeDate = #{tradedate}


AND t.state = #{state}


AND t.Goodsid = #{goodsid}

mapper.delete:
DELETE FROM
    trade


    id = #{id}


AND FromUInfoid = #{fromuinfoid}


AND ToUInfoid = #{touinfoid}


AND TradeDate = #{tradedate}


AND state = #{state}


AND Goodsid = #{goodsid}

mapper.update:
UPDATE trade
id = #{id}, FromUInfoid = #{fromuinfoid}, ToUInfoid = #{touinfoid}, TradeDate = #{tradedate}, state = #{state}, Goodsid = #{goodsid}
WHERE id = #{id}
