mapper.select:
SELECT
    t.id AS id, 
    t.uname AS uname,
    t.img AS img,
    t.pwd AS pwd, 
    t.state AS state, 
    t.del_flag AS del_flag,
    t.UpdateDate AS updatedate, 
    t.DeleteDate AS deletedate
FROM
    user t

    t.del_flag = '0'

AND t.id = #{id}


AND t.uname = #{uname}


AND t.img = #{img}


AND t.pwd = #{pwd}


AND t.state = #{state}


AND t.del_flag = #{del_flag}


AND t.UpdateDate = #{updatedate}


AND t.DeleteDate = #{deletedate}

select u.id as id , u.uname as username, u.img as img, u.pwd as password, u.state as Userlevel, u.del_flag as delFlag, u.UpdateDate as updatedate, u.DeleteDate as deletedate, f.id as uid, f.name as name, f.age as age , f.IDCard as idCard, f.phone as phone , f.moblie as moblie, f.sex as sex, f.address as address, f.balance as balance, f.state as state, f.Level as level, f.LLoginDate as llogindate from user u left join userinfo f on u.id = f.Uid
u.del_flag = '0' and f.del_flag='0'
AND u.id like #{id}
AND u.uname like #{username}
AND u.img like #{img}
AND u.pwd = #{password}
AND u.state = #{Userlevel}
AND u.del_flag = #{delFlag}
AND u.UpdateDate = #{updatedate}
AND u.DeleteDate = #{deletedate}

select u.id as id , u.uname as username, u.img as img, u.pwd as password, u.state as Userlevel, u.del_flag as delFlag, u.UpdateDate as updatedate, u.DeleteDate as deletedate, f.id as uid, f.name as name, f.age as age , f.IDCard as idCard, f.phone as phone , f.moblie as moblie, f.sex as sex, f.address as address, f.balance as balance, f.state as state, f.Level as level, f.LLoginDate as llogindate from user u left join userinfo f on u.id = f.Uid
u.del_flag = '1' and f.del_flag='1'
AND u.id like #{id}
AND u.uname like #{username}
AND u.img like #{img}
AND u.pwd = #{password}
AND u.state = #{Userlevel}
AND u.del_flag = #{delFlag}
AND u.UpdateDate = #{updatedate}
AND u.DeleteDate = #{deletedate}
ORDER BY ${page.orderBy}
ORDER BY f.LLoginDate desc

mapper.delete:
DELETE FROM
    user


    id = #{id}


AND uname = #{uname}


AND pwd = #{pwd}


AND state = #{state}


AND del_flag = #{del_flag}


AND UpdateDate = #{updatedate}


AND DeleteDate = #{deletedate}

mapper.update:
UPDATE user
id = #{id}, uname = #{uname}, img = #{img}, pwd = #{pwd}, state = #{state}, del_flag = #{del_flag}, UpdateDate = #{updatedate}, DeleteDate = #{deletedate}
WHERE id = #{id}
